import { useEffect, useState} from "react"
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import DeleteButton from "./DeleteButton";
import { useNavigate } from 'react-router-dom';
import './styles.css'
import Header from "./Header";

const Book = () => {
    const [book, setBook] = useState([]);
    const navigate = useNavigate();
   

    const fetchData = async () => {
      try {
       const token = localStorage.getItem("token");
       console.log("Token:", token);
   
       const response = await fetch("http://localhost:8080/v1/book", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Authorization": token,
      },
    });
   
       if (!response.ok) {
          const errorData = await response.json();
          navigate('/', { state: { error: `${errorData.message} Please Login` } });
          localStorage.removeItem("token");
          
       }
   
       const data = await response.json();
       setBook(data.data);
      } catch (error) {

        console.error("Fetch error:", error);
        // navigate('/', { state: { error: `${error}` } });
      }
    };

    
   
    useEffect(() => {
       fetchData();
    }, []);

    const handleAddBookClick = () => {
      // Navigasi ke halaman form
      navigate("/v1/book/add");
  };
  
function CardBook(props){
  const handleUpdateBookClick = ()=>{
    navigate('/v1/book/update', {
      state: {
        id: props.id,
        title: props.title,
        authro: props.author,
        release: props.release,
       
      }
    });
    
  }
    return (
      <Card className="mb-4" style={{ width: '18rem', border: '1px solid #ddd', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', transition: 'transform 0.3s ease-in-out' }}>
      <Card.Body>
        <Card.Title style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#333' }}>{props.title}</Card.Title>
        <Card.Text style={{ color: '#555' }}>
          <ul>
            <li>Author: {props.author}</li>
            <li>Release: {props.release}</li> 
          </ul>
        </Card.Text>
        <Button variant="primary" onClick={handleUpdateBookClick} style={{ marginTop: '10px' }}>
          Edit
        </Button>
        <DeleteButton id={props.id} />
      </Card.Body>
    </Card>
    
      );
}

  
return (
  <div className="full-height  content-container" >
      <Header/>
    <div className="container mt-4">
    <Button variant="primary"  onClick={handleAddBookClick} >Add Book</Button>
        <div className="row mt-4">
        {book.map((buku)=>  {
            return (
                <div className="col-3">
          
                <CardBook id={buku.book_id} 
                title={buku.book_title}
                author={buku.book_author} 
                release={buku.book_release}
               />

                </div>
            )
        })}
        </div>
       
    </div>
    </div>
)
}




export default Book;
