// import React from 'react'

import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './style.css'


const login = async (username, password) => {
  try {
    const response = await fetch("http://localhost:8080/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username,
        password,
      }),
    });

    if (response.ok) {
      // Login berhasil
      const data = await response.json();
      const token = data.token;
      // Simpan token di local storage
      localStorage.setItem("token", token);
    } else {
      // Login gagal
      alert("Username atau password salah");
    }

    return response;
  } catch (error) {
    console.error("Error during login:", error);
    throw error;
  }
};


const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const location = useLocation();
  const { state } = location;

  const handleLogin = async () => {
    try {
      const response = await login(username, password);
  
      if (response.ok) {
        // Login berhasil
        navigate("/v1/book");
      } else {
        // Login gagal
      }
    } catch (error) {
      console.error("Error during login:", error);
    }
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#f0f0f0' }}>
  <div className="login-container" style={{ padding: '20px', borderRadius: '8px', boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)', backgroundColor: '#fff', maxWidth: '400px', width: '100%', textAlign: 'center' }}>
    <h2 style={{ color: '#333', marginBottom: '20px', fontSize: '24px' }}>Masuk</h2>
    <input
      type="text"
      placeholder="Nama Pengguna"
      value={username}
      onChange={(e) => setUsername(e.target.value)}
      className="input-field"
      style={{ marginBottom: '15px', padding: '10px', border: '1px solid #ddd', borderRadius: '5px', width: '100%', boxSizing: 'border-box' }}
    />
    <input
      type="password"
      placeholder="Kata Sandi"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
      className="input-field"
      style={{ marginBottom: '15px', padding: '10px', border: '1px solid #ddd', borderRadius: '5px', width: '100%', boxSizing: 'border-box' }}
    />
    <button onClick={handleLogin} className="login-button" style={{ backgroundColor: '#4CAF50', color: '#fff', padding: '10px 20px', borderRadius: '5px', cursor: 'pointer', border: 'none', fontSize: '16px' }}>
      Masuk
    </button>
    {state && state.error && (
      <p style={{ color: "#ff0000", marginTop: '15px', fontSize: '14px' }}>{state.error}</p>
    )}
  </div>
</div>



  );
};


export default Login
