import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Header from './Header';
import { useNavigate, useLocation, Link } from 'react-router-dom';


  // Fungsi khusus untuk menangani efek samping (side effect)

const add = async (title, author, release,) => {
    try {
      const token = localStorage.getItem("token");
      console.log("Token:", token);
      const response = await fetch("http://localhost:8080/v1/book", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": token,
        },
        body: JSON.stringify({
          book_title: title,
          book_author: author,
          book_release: release,
        
        }),
      });
      return response;
    } catch (error) {
      throw error;
    }
  };
  
const AddBookForm = () => {
  const [title, settitle] = useState("");
  const [author, setauthor] = useState("");
  const [release, setrelease] = useState("");
  const navigate = useNavigate();
  const location = useLocation();
  const { state } = location;

  const handleTokenCheck = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      // Redirect ke halaman login jika token tidak ada
      navigate('/', { state: { error: 'not authorized Please Login' } });
    }
  };
   // Memanggil fungsi khusus dalam useEffect
   useEffect(() => {
    handleTokenCheck();
  }, []);

  const handleAdd = async () => {
    try {
      const response = await add(title, author, release);

      if (response.ok) {
        // Add Success
        alert('Buku Berhasil Ditambahkan');
        navigate('/v1/book')
      } else if (response.status === 400) {
        // Unauthorized, token tidak valid
        alert('Data Gagal Ditambahkan,Perikasa Kolom Jangan Ada Yang Kosong');
      } else {
        const errorData = await response.json();
        navigate('/', { state: { error: `${errorData.messauthor} Please Login` } });
        localStorage.removeItem("token");
       
      }
    } catch (error) {
      console.error('Error during login:', error);
      
    }
  };

  return (
    <div>
      <Header />
      <Form
        style={{
          width: '50%',
          margin: 'auto',
          backgroundColor:'white',
          marginTop: '50px',
          padding: '20px',
          border: '1px solid #ccc',
          borderRadius: '8px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        }}
      >
        <h3 classtitle="text-center">Add Book Data</h3>
        <Form.Group classtitle="mb-3" controlId="formtitle">
          <Form.Label>title</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter title"
            title="title"
            value={title}
            onChange={(e) => settitle(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group classtitle="mb-3" controlId="formauthor">
          <Form.Label>author</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter author"
            title="author"
            value={author}
            onChange={(e) => setauthor(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group classtitle="mb-3" controlId="formrelease">
          <Form.Label>release</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter release"
            title="release"
            value={release}
            onChange={(e) => setrelease(e.target.value)}
            required
          />
        </Form.Group>
        

        <Button variant="primary" onClick={handleAdd}>
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default AddBookForm;
