// UpdateBookForm.jsx

import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Header from './Header';
import './Styles/UpdateBookForm.css';
import { useNavigate, useLocation } from 'react-router-dom';

const update = async (id, title, author, release) => {
  try {
    const token = localStorage.getItem("token");
    const response = await fetch(`http://localhost:8080/v1/book/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "Authorization": token,
      },
      body: JSON.stringify({
        book_title: title,
        book_author: author,
        book_release: release,
      }),
    });
    return response;
  } catch (error) {
    throw error;
  }
};

const UpdateBookForm = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [release, setRelease] = useState("");
  const navigate = useNavigate();
  const location = useLocation();
  const { state } = location;

  const handleTokenCheck = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate('/', { state: { error: 'not authorized Please Login' } });
    }
  };

  useEffect(() => {
    handleTokenCheck();
  }, []);

  useEffect(() => {
    if (state) {
      setTitle(state.title || "");
      setAuthor(state.author || "");
      setRelease(state.release || "");
    }
  }, [state]);

  const handleUpdate = async () => {
    const id = state.id;
    try {
      const response = await update(id, title, author, release);

      if (response.ok) {
        alert('Data Berhasil Diupdate');
        navigate('/v1/book');
      } else if (response.status === 400) {
        alert('Data Gagal Diupdate, Periksa Kolom Jangan Ada Yang Kosong');
      } else {
        const errorData = await response.json();
        navigate('/', { state: { error: `${errorData.message} Please Login` } });
        localStorage.removeItem("token");
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div>
      <Header />
      <Form className="form-container">
        <h3>Update Book Data</h3>
        <Form.Group className="form-group" controlId="formTitle">
          <Form.Label className="form-label">Title</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter title"
            name="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="form-control"
          />
        </Form.Group>

        <Form.Group className="form-group" controlId="formAuthor">
          <Form.Label className="form-label">Author</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Author"
            name="author"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            required
            className="form-control"
          />
        </Form.Group>

        <Form.Group className="form-group" controlId="formRelease">
          <Form.Label className="form-label">Release</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Release"
            name="release"
            value={release}
            onChange={(e) => setRelease(e.target.value)}
            required
            className="form-control"
          />
        </Form.Group>

        <Button variant="primary" onClick={handleUpdate} className="btn-primary">
          Submit
        </Button>
      </Form>
    </div>
  );
};

export default UpdateBookForm;
